/**
 * Version v0.1.3
 *
 * Wait for the delay set with function parameters
 *
 * @param $nHours
 * @param $nMinutes
 * @param $nSeconds
 * @param $nMilliSeconds
 */
function Sleep (
    $nHours        = 0,
    $nMinutes      = 0,
    $nSeconds      = 1,
    $nMilliSeconds = 0
) {
    let nDelayMs = (($nHours * 3600) + ($nMinutes * 60) + $nSeconds) * 1000 + $nMilliSeconds;

    Delay(nDelayMs);
}

module.exports = Sleep;